const crypto = require('crypto');

const Handler = function () {
};

jcrypto = module.exports = Handler;

jcrypto.encrypt = function (sSrc, keys, ivKeys, algorithm, charType) {
    const cipher = crypto.createCipheriv(algorithm, keys, ivKeys);
    return cipher.update(sSrc, 'utf-8', charType) + cipher.final(charType);
};

jcrypto.decrypt = function (sSrc, keys, ivKeys, algorithm, charType) {
    const cipher = crypto.createDecipheriv(algorithm, keys, ivKeys);
    return cipher.update(sSrc, charType, 'utf-8') + cipher.final('utf-8');
};